import{_ as t,o,c as d,ag as r}from"./chunks/framework.DjYIiZ32.js";const u=JSON.parse('{"title":"Server Config File","titleTemplate":"Simple Voice Chat","description":"","frontmatter":{"titleTemplate":"Simple Voice Chat","aside":false},"headers":[],"relativePath":"minecraft/voicechat/wiki/server_config.md","filePath":"minecraft/voicechat/wiki/server_config.md"}'),c={name:"minecraft/voicechat/wiki/server_config.md"};function a(i,e,s,n,h,l){return o(),d("div",null,[...e[0]||(e[0]=[r('<h1 id="server-config-file" tabindex="-1">Server Config File <a class="header-anchor" href="#server-config-file" aria-label="Permalink to &quot;Server Config File&quot;">â€‹</a></h1><p><strong>Fabric/NeoForge/Forge/Quilt</strong></p><p><code>config/voicechat/voicechat-server.properties</code></p><p><strong>Bukkit/Spigot/Paper</strong></p><p><code>plugins/voicechat/voicechat-server.properties</code></p><table tabindex="0"><thead><tr><th>Config key</th><th>Description</th><th>Default value</th></tr></thead><tbody><tr><td><code>port</code></td><td>The port number to use for the voice chat communication.<br>Audio packets are always transmitted via the UDP protocol on the port number specified here, independently of other networking used for the game server.<br>Set this to <code>-1</code> to use the same port number that is used by the Minecraft server.<br>However, it is strongly recommended NOT to use the same port number because UDP on it is also used by default for the server query. Doing so may crash the server!</td><td><code>24454</code></td></tr><tr><td><code>bind_address</code></td><td>The server IP address to bind the voice chat to<br>Leave blank to use the <code>server-ip</code> property from the <code>server.properties</code> config file<br>To bind to the wildcard IP address, use <code>*</code></td><td><code></code></td></tr><tr><td><code>max_voice_distance</code></td><td>The distance to which the voice can be heard</td><td><code>48</code></td></tr><tr><td><code>whisper_distance</code></td><td>The distance to which the voice can be heard when whispering</td><td><code>24</code></td></tr><tr><td><code>codec</code></td><td>The Opus codec<br>Valid values are <code>VOIP</code>, <code>AUDIO</code> and <code>RESTRICTED_LOWDELAY</code></td><td><code>VOIP</code></td></tr><tr><td><code>mtu_size</code></td><td>The maximum size that audio packets are allowed to have (in bytes)<br>Set this to a lower value if audio packets don&#39;t arrive</td><td><code>1024</code></td></tr><tr><td><code>keep_alive</code></td><td>The frequency at which keep-alive packets are sent (in milliseconds)<br>Setting this to a higher value may result in timeouts</td><td><code>1000</code></td></tr><tr><td><code>enable_groups</code></td><td>If group chats are allowed</td><td><code>true</code></td></tr><tr><td><code>voice_host</code></td><td>The hostname that clients should use to connect to the voice chat<br>This may also include a port, e.g. &#39;example.com:24454&#39; or just a port, e.g. &#39;24454&#39;<br>Do <strong>NOT</strong> change this value if you don&#39;t know what you&#39;re doing</td><td><code></code></td></tr><tr><td><code>allow_recording</code></td><td>If players are allowed to record the voice chat audio</td><td><code>true</code></td></tr><tr><td><code>spectator_interaction</code></td><td>If spectators are allowed to talk to other players</td><td><code>false</code></td></tr><tr><td><code>spectator_player_possession</code></td><td>If spectators can talk to players they are spectating</td><td><code>false</code></td></tr><tr><td><code>force_voice_chat</code></td><td>If players without the voice chat mod should be kicked from the server</td><td><code>false</code></td></tr><tr><td><code>login_timeout</code></td><td>The amount of time the server should wait to check if a player has the mod installed (in milliseconds)<br>Only relevant when <code>force_voice_chat</code> is set to <code>true</code></td><td><code>10 000</code></td></tr><tr><td><code>broadcast_range</code></td><td>The range in which the voice chat should broadcast audio to<br>A value less than0 means <code>max_voice_distance</code></td><td><code>-1.0</code></td></tr><tr><td><code>allow_pings</code></td><td>If the voice chat server should reply to external pings</td><td><code>true</code></td></tr><tr><td><code>use_natives</code></td><td>If the mod should load native libraries on dedicated servers<br>This is mostly relevant for voice chat addons</td><td><code>true</code></td></tr></tbody></table>',6)])])}const v=t(c,[["render",a]]);export{u as __pageData,v as default};
